import React from 'react'
import styled from 'styled-components'




const Section = styled.section`
    /* background-color: #fff; */
    width: 100%;
    min-height: 100vh;
    padding: 70px 70px;
    @media (max-width: 64em){
        padding: 40px 12px;
    }
`
const Title = styled.h1`
    font-weight: 800;
    color: #093691;
    border-bottom: 2px solid #093691;
    width: fit-content;
    margin: 0 auto;
    font-family: 'Oswald', sans-serif;
`
const Container = styled.div`
background-color: #2e91f9;
padding: 0px 50px;
border-radius: 10px;
display: flex;
justify-content: center;
align-items: center;
flex-direction: row;
width: 100%;
height: 75vh;
margin-top: 40px;
@media (max-width: 54em){
    flex-direction: column;
    gap: 40px;
    height: auto;
    padding: 12px 20px;
}
div{
    width: 60%;
    @media (max-width: 64em){
        width: 100%;
    }
    
}
`
const SubTitle = styled.h2`
color: #fff;
border-bottom: 1px solid #fff;
font-size: 34px;
font-weight: 800;
width: fit-content;
margin-bottom: 16px;
`
const SubText = styled.p`
color: #fff;
font-weight: bolder;
margin-top: 5px;
text-align: justify;
`
const BtnContainer = styled.div`
/* background-color: #fff;
display: flex;
flex-direction: column;
gap: 8px; */
/* background-color: #34495e; */
display: flex;
flex-direction: column;
gap: 8px;
padding: 12px 12px;
border-radius: 12px;

@media (max-width: 64em){
    padding: 4px;
}
`
const Btn = styled.button`
width: 40px;
height: 40px;
padding: 0 12px;
background-color: #fff;
color: #000;
font-weight: bold;
outline: none;
border: none;
border-radius: 10px;
overflow: hidden;
display: flex;
align-items: center;
text-align: center;
transition: all .3s ease;
margin-bottom: 10px;
cursor: pointer;


/* display: inline-block;
outline: none;
border: none;
color: #fff;
font-weight: 600;
background-color: #555;
