import React, { useEffect, useState } from 'react'

const Nav1 = () => {
    const [sticky,setSticky]=useState(true)
    const [open,setOpen]=useState(true)
    const menuLinks = [
        {name: "HOME", link: "#home"},
        {name: "ABOUT", link: "#about"},
        {name: "SKILLS", link: "#skills"},
        {name: "PROJECTS", link: "#projects"},
        {name: "CONTACT", link: "#contact"},
    ]
    useEffect(()=>{
        window.addEventListener("scroll",()=>{
            // const nav = document.querySelector("nav")
            window.scrollY > 0 ? setSticky(true):setSticky(false)  
        })
    },[])
  return (
    <nav className={`z-[999] fixed left-0 top-0 w-full ${sticky ? "bg-white/60 text-gray-900":"text-white"}`}>
        <div className='flex items-center justify-between'>
            <div  className='mx-7'>
                <h4 className='text-4xl uppercase font-bold '>
                    A<span className='text-cyan-600'>L</span>I
                </h4>
            </div>
            <div className={`${sticky ? "md:bg-white/0 bg-white":"bg-white"} text-gray-900 md:block px-7 py-2 font-medium bg-white hidden rounded-bl-full`}>
                <ul className='flex items-center gap-1 py-2 text-lg'>
                    {
                        menuLinks?.map((menu, i)=>(
                            <li key={i} className="px-6 hover:text-cyan-600">
                                <a href={menu?.link}>{menu?.name}</a>
                            </li>
                        ))
                    }
                </ul>
            </div>
            <div className='text-3xl md:hidden m-5'>
                <ion-icon name="menu"></ion-icon>
            </div>
            <div>
                <ul className='flex flex-col justify-center h-full gap-10'>
                    {
                        menuLinks?.map((menu,i)=>{
                            <li key={i} >
                                <a href={menu?.link} className="text-white cursor-pointer">{menu?.name}</a>
                            </li>
                        })
                    }
                </ul>
            </div>
        </div>
    </nav>
  )
}

export default Nav1
